e { navigate( 'member-enter.php', $conn->error ); } $conn->close(); } else { navigate( 'member-enter.php?status=fail' ); } $conn->close(); } ?> <?php if ( isset( $_GET[ 'status' ] ) ) { if ( $_GET[ 'status' ] == "success" ) { print( '<script type="text/javascript"> showmessage("Recode successfully Deleted"); </script>' ); } if ( $_GET[ 'status' ] == "fail" ) { print( '<script type="text/javascript"> showmessage("Incorrect Username/Password"); </script>' ); }   } if ( isset( $_GET[ 'msg' ] ) ) { echo($_GET[ 'msg' ]); print( '<script type="text/javascript"> showmessage("' . $_GET[ 'msg' ] . '"); </script>' ); } ?> </div> </div> <br /> <!-- Footer --> <footer class="footer text-center text-white fixed-bottom"> <div class="text-center p-0" style="background-color: rgba(0, 0, 0, 0.774)" > Made with ❤️ by HNDIT 2019 batch. </div> </footer> <!-- MDB --> <script type="text/javascript" src="https://cdnjs.cloudflare.com/ajax/libs/mdb-ui-kit/3.5.0/mdb.min.js" ></script> </body> </html> 